    <script>
        $(document).ready(function() {

            var isPhoneVerified = false;

            $("#repeat-otp").on("click", (e) => {
                e.preventDefault();
                $("#phoneNumber").prop("disabled", false);
                $(".input-otp").prop("hidden", true);
                $("#repeat-otp").prop("hidden", true);
                $("#verify-otp").prop("hidden", true);
                $("#send-otp").prop("hidden", false);
            });

            $("#verify-otp").on("click", (e) => {
                e.preventDefault();

                // Get the value from the input with id 'phoneNumber'
                const otp = $("#otp").val();
                const phone_number = $("#phoneNumber").val();

                // Make a POST request to the /api/otp/send endpoint
                $.ajax({
                    url: '/api/otp/verify',
                    type: 'POST',
                    data: {
                        phone_number,
                        otp
                    },
                    dataType: 'JSON',
                    success: function(response) {
                        if (response.error == 0) {
                            $("#send-otp").prop("hidden", true);
                            $("#verify-otp").prop("hidden", true);
                            $("#repeat-otp").prop("hidden", true);
                            $(".input-otp").prop("hidden", true);

                            $(".form-hidden").each(function(index, element) {
                                $(element).removeClass("form-hidden");

                            });

                            Swal.fire({
                                title: "Sukses",
                                text: "Verifikasi otp berhasil!",
                                icon: "success"
                            });

                            isPhoneVerified = true;

                        } else {
                            Swal.fire({
                                title: "Gagal",
                                text: response.message,
                                icon: "error"
                            });
                        }
                    },
                    error: function(error) {
                        Swal.fire({
                            title: "Gagal",
                            text: "OTP gagal diverifikasi, coba lagi yuk",
                            icon: "error"
                        });
                    }
                });
            });

            $("#send-otp").on("click", (e) => {
                e.preventDefault();

                // Get the value from the input with id 'phoneNumber'
                const phone_number = $("#phoneNumber").val();

                // Make a POST request to the /api/otp/send endpoint
                $.ajax({
                    url: '/api/otp/send',
                    type: 'POST',
                    data: {
                        phone_number: phone_number
                    },
                    dataType: 'json',
                    success: function(response) {
                        if (response.error == 0) {
                            $("#phoneNumber").prop("disabled", true);
                            $("#send-otp").prop("hidden", true);
                            $(".input-otp").prop("hidden", false);
                            $("#verify-otp").prop("hidden", false);
                            $("#repeat-otp").prop("hidden", false);

                            Swal.fire({
                                title: "Sukses",
                                text: "OTP Berhasil dikirim, cek wa kamu yuk",
                                icon: "success"
                            });
                        } else {
                            Swal.fire({
                                title: "Gagal",
                                text: response.message,
                                icon: "error"
                            });
                        }
                    },
                    error: function(error) {
                        Swal.fire({
                            title: "Gagal",
                            text: "OTP gagal dikirim, coba lagi yuk",
                            icon: "error"
                        });
                    }
                });
            });

            $("#make-payment").on("click", (e) => {

                e.preventDefault();

                const request_id = $("#request_id").val();
                if ($.trim(request_id) === "") {

                    Swal.fire({
                        title: "Gagal",
                        text: "File belum di upload, yuk cek file kamu!",
                        icon: "error"
                    });

                    $("#step1").click();

                    return;
                }

                const phone_number = $("#phoneNumber").val();
                if (!isPhoneVerified) {

                    Swal.fire({
                        title: "Gagal",
                        text: "Nomor wa kamu belum di verif nih, cek langkah kedua yuk!",
                        icon: "error"
                    });

                    $("#step2").click();

                    return;
                }

                /* File purpose */
                const title = $("#fileTitle").val();
                const firstAuthorName = $("#firstAuthorName").val();
                const secondAuthorName = $("#secondAuthorName").val();


                $.ajax({
                    url: '/api/turnitin/make-payment',
                    type: 'POST',
                    data: JSON.stringify({
                        request_id,
                        phone_number,
                        file: {
                            title: title,
                            first_author: firstAuthorName,
                            second_author: secondAuthorName
                        }
                    }),
                    contentType: "application/json; charset=utf-8",
                    dataType: 'JSON',
                    success: function(response) {
                        if (response.error == 0) {

                            Swal.fire({
                                title: "Sukses",
                                text: "Berhasil, kamu akan segera di alihkan",
                                icon: "success"
                            });
                            window.location.href = response.url;

                        } else {
                            Swal.fire({
                                title: "Gagal",
                                text: "Link pembayaran gagal dibuat, coba lagi yuk",
                                icon: "error"
                            });
                        }
                    },
                    error: function(error) {
                        Swal.fire({
                            title: "Gagal",
                            text: "Gagal buat link pembayaran nih, coba lagi yuk",
                            icon: "error"
                        });
                    }
                });
            });


            const setTracking = (trackingStep) => {
                for (var i = 3; i >= 1; i--) {
                    if (i > trackingStep) {
                        $("#step" + i).removeClass("active");
                        continue;
                    }
                    $("#step" + i).addClass("active");
                }
            }

            const setBody = (currentStep) => {
                $(".order-steps.active").removeClass("active");
                $(".order-steps.steps-" + currentStep).addClass("active");
            }

            $(".order-steps-track").on("click", (e) => {
                e.preventDefault();

                const currentStep = parseInt($(e.currentTarget).data("steps"));

                setTracking(currentStep);
                setBody(currentStep);
            });

            // Initial setup
            $(".order-steps:first").show();

            // Handle submit button click
            $(".next-step").on("click", (e) => {
                e.preventDefault();

                currentStep = parseInt($(e.currentTarget).data("steps"));

                setTracking(currentStep);
                setBody(currentStep);
            });

        });
    </script>

